"use client"

import { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import { orderService } from "../../services/orderService"

function OrdersList() {
  const [orders, setOrders] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")

  useEffect(() => {
    fetchOrders()
  }, [])

  const fetchOrders = async () => {
    setLoading(true)
    try {
      const data = await orderService.getUserOrders()
      setOrders(data)
    } catch (error) {
      setError(error.message)
    } finally {
      setLoading(false)
    }
  }

  if (loading) {
    return <div className="loading">Loading orders...</div>
  }

  if (error) {
    return <div className="alert alert-danger">{error}</div>
  }

  if (orders.length === 0) {
    return (
      <div className="no-orders">
        <h2>My Orders</h2>
        <div className="alert alert-info">You haven't placed any orders yet.</div>
        <Link to="/books" className="btn btn-primary">
          Browse Books
        </Link>
      </div>
    )
  }

  return (
    <div className="orders-list-container">
      <h2>My Orders</h2>
      <div className="orders-list">
        {orders.map((order) => (
          <div key={order.id} className={`order-card status-${order.status.toLowerCase()}`}>
            <div className="order-card-header">
              <div className="order-number">Order #{order.id}</div>
              <div className="order-status">
                <span className={`status-badge status-${order.status.toLowerCase()}`}>{order.status}</span>
              </div>
            </div>
            <div className="order-card-body">
              <div className="order-info">
                <div className="info-row">
                  <span className="info-label">Date:</span>
                  <span className="info-value">{new Date(order.orderDate).toLocaleDateString()}</span>
                </div>
                <div className="info-row">
                  <span className="info-label">Items:</span>
                  <span className="info-value">{order.itemCount} items</span>
                </div>
                <div className="info-row">
                  <span className="info-label">Total:</span>
                  <span className="info-value">${order.total.toFixed(2)}</span>
                </div>
                <div className="info-row">
                  <span className="info-label">Claim Code:</span>
                  <span className="info-value claim-code">{order.claimCode}</span>
                </div>
                {order.status === "Processed" && (
                  <div className="processed-notification">
                    <i className="check-icon">✓</i> This order has been processed and is ready for pickup!
                  </div>
                )}
              </div>
            </div>
            <div className="order-card-footer">
              <Link to={`/orders/${order.id}`} className="btn btn-primary">
                View Details
              </Link>
              {order.status === "Pending" && (
                <button
                  className="btn btn-danger"
                  onClick={async () => {
                    if (window.confirm("Are you sure you want to cancel this order?")) {
                      try {
                        await orderService.cancelOrder(order.id)
                        // Refresh orders
                        fetchOrders()
                      } catch (error) {
                        setError(error.message)
                      }
                    }
                  }}
                >
                  Cancel
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}

export default OrdersList
