"use client"

import { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import { orderService } from "../../services/orderService"

function OrdersList() {
  const [orders, setOrders] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")

  useEffect(() => {
    fetchOrders()
  }, [])

  const fetchOrders = async () => {
    setLoading(true)
    try {
      const data = await orderService.getOrders()
      setOrders(data)
    } catch (error) {
      setError(error.message)
    } finally {
      setLoading(false)
    }
  }

  const handleCancelOrder = async (orderId) => {
    if (window.confirm("Are you sure you want to cancel this order?")) {
      try {
        await orderService.cancelOrder(orderId)
        // Refresh orders list
        fetchOrders()
      } catch (error) {
        setError(error.message)
      }
    }
  }

  if (loading) {
    return <div className="loading">Loading orders...</div>
  }

  if (error) {
    return <div className="alert alert-danger">{error}</div>
  }

  if (orders.length === 0) {
    return (
      <div className="orders-page">
        <h2 className="orders-title">My Orders</h2>
        <div className="empty-orders">
          <p>You haven't placed any orders yet.</p>
          <Link to="/user-panel" className="btn btn-primary">
            Browse Books
          </Link>
        </div>
      </div>
    )
  }

  return (
    <div className="orders-page">
      <h2 className="orders-title">My Orders</h2>

      <div className="orders-list">
        {orders.map((order) => (
          <div key={order.id} className="order-card">
            <div className="order-header">
              <div className="order-info">
                <h3 className="order-number">Order #{order.id}</h3>
                <p className="order-date">{new Date(order.orderDate).toLocaleDateString()}</p>
              </div>
              <div className="order-status">
                <span className={`status-badge status-${order.status.toLowerCase()}`}>{order.status}</span>
              </div>
            </div>
            <div className="order-details">
              <div className="order-summary">
                <p className="order-items-count">{order.itemCount} item(s)</p>
                <p className="order-total">${order.total.toFixed(2)}</p>
              </div>
              <div className="order-claim-code">
                <span className="claim-code-label">Claim Code:</span>
                <span className="claim-code">{order.claimCode}</span>
              </div>
            </div>
            <div className="order-actions">
              <Link to={`/orders/${order.id}`} className="btn btn-primary btn-sm">
                View Details
              </Link>
              {order.canCancel && (
                <button className="btn btn-danger btn-sm" onClick={() => handleCancelOrder(order.id)}>
                  Cancel Order
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}

export default OrdersList
