"use client"

const BannerList = ({ banners, onEdit, onDelete, onRefresh }) => {
  const formatDate = (dateString) => {
    const date = new Date(dateString)
    return date.toLocaleDateString()
  }

  const getBannerStatus = (banner) => {
    const now = new Date()
    const startDate = new Date(banner.startDate)
    const endDate = new Date(banner.endDate)

    if (now < startDate) {
      return "Scheduled"
    } else if (now > endDate) {
      return "Expired"
    } else {
      return "Active"
    }
  }

  const getStatusClass = (status) => {
    switch (status) {
      case "Active":
        return "status-active"
      case "Scheduled":
        return "status-scheduled"
      case "Expired":
        return "status-expired"
      default:
        return ""
    }
  }

  return (
    <div className="banner-list-container">
      <div className="banner-list-header">
        <h3>Banner Announcements</h3>
        <button className="btn btn-secondary btn-sm" onClick={onRefresh}>
          Refresh
        </button>
      </div>

      {banners.length === 0 ? (
        <div className="no-banners">No banners found. Create a new banner to get started.</div>
      ) : (
        <div className="table-responsive">
          <table className="banner-table">
            <thead>
              <tr>
                <th>Message</th>
                <th>Start Date</th>
                <th>End Date</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {banners.map((banner) => {
                const status = getBannerStatus(banner)
                return (
                  <tr key={banner.id}>
                    <td className="banner-message-cell">
                      <div
                        className="banner-message-preview"
                        style={{
                          backgroundColor: banner.backgroundColor,
                          color: banner.textColor,
                        }}
                      >
                        {banner.message.length > 100 ? `${banner.message.substring(0, 100)}...` : banner.message}
                      </div>
                    </td>
                    <td>{formatDate(banner.startDate)}</td>
                    <td>{formatDate(banner.endDate)}</td>
                    <td>
                      <span className={`banner-status ${getStatusClass(status)}`}>{status}</span>
                    </td>
                    <td>
                      <div className="banner-actions">
                        <button className="btn btn-primary btn-sm" onClick={() => onEdit(banner)} title="Edit Banner">
                          Edit
                        </button>
                        <button
                          className="btn btn-danger btn-sm"
                          onClick={() => onDelete(banner.id)}
                          title="Delete Banner"
                        >
                          Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                )
              })}
            </tbody>
          </table>
        </div>
      )}
    </div>
  )
}

export default BannerList
