'use client'

import { useState, useEffect } from 'react'
import adminBookService from '@/services/adminBookService'
import { toast } from 'react-toastify'

const BookForm = ({ book = null, isEditMode = false, onSuccess, onCancel }) => {
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    genre: '',
    price: '',
    stock: '',
    coverImageUrl: '',
    description: '',
    isOnSale: false,
    discountPercentage: '',
    discountEndDate: ''
  })

  const [errors, setErrors] = useState({})
  const [isSubmitting, setIsSubmitting] = useState(false)

  useEffect(() => {
    if (isEditMode && book) {
      setFormData({
        title: book.title || '',
        author: book.author || '',
        genre: book.genre || '',
        price: book.price || '',
        stock: book.stock || '',
        coverImageUrl: book.coverImageUrl || '',
        description: book.description || '',
        isOnSale: book.isOnSale || false,
        discountPercentage: book.discountPercentage || '',
        discountEndDate: book.discountEndDate || ''
      })
    }
  }, [isEditMode, book])

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target
    setFormData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }))
  }

  const validateForm = () => {
    const newErrors = {}

    if (!formData.title.trim()) newErrors.title = 'Title is required'
    if (!formData.author.trim()) newErrors.author = 'Author is required'
    if (!formData.genre.trim()) newErrors.genre = 'Genre is required'
    if (!formData.price || isNaN(formData.price) || Number(formData.price) <= 0)
      newErrors.price = 'Valid price is required'
    if (!formData.stock || isNaN(formData.stock) || Number(formData.stock) < 0)
      newErrors.stock = 'Valid stock is required'
    if (!formData.coverImageUrl.trim()) newErrors.coverImageUrl = 'Cover image URL is required'
    if (!formData.description.trim()) newErrors.description = 'Description is required'

    if (formData.isOnSale) {
      if (
        !formData.discountPercentage ||
        isNaN(formData.discountPercentage) ||
        Number(formData.discountPercentage) <= 0 ||
        Number(formData.discountPercentage) > 100
      ) {
        newErrors.discountPercentage = 'Valid discount percentage (1-100) is required'
      }
      if (!formData.discountEndDate) {
        newErrors.discountEndDate = 'Discount end date is required'
      }
    }

    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    if (!validateForm()) return

    setIsSubmitting(true)
    try {
      if (isEditMode) {
        await adminBookService.updateBook(book.id, formData)
        toast.success('Book updated successfully')
      } else {
        await adminBookService.addBook(formData)
        toast.success('Book added successfully')
      }
      onSuccess()
    } catch (error) {
      console.error(error)
      toast.error('Something went wrong')
    } finally {
      setIsSubmitting(false)
    }
  }

  return (
    <form onSubmit={handleSubmit} className="book-form" style={{ maxWidth: '700px', margin: 'auto' }}>
      <h2>{isEditMode ? 'Edit Book' : 'Add New Book'}</h2>
      <div className="form-grid">
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            name="title"
            className={`form-control ${errors.title ? 'is-invalid' : ''}`}
            value={formData.title}
            onChange={handleChange}
          />
          {errors.title && <div className="invalid-feedback">{errors.title}</div>}
        </div>

        <div className="form-group">
          <label htmlFor="author">Author</label>
          <input
            type="text"
            id="author"
            name="author"
            className={`form-control ${errors.author ? 'is-invalid' : ''}`}
            value={formData.author}
            onChange={handleChange}
          />
          {errors.author && <div className="invalid-feedback">{errors.author}</div>}
        </div>

        <div className="form-group">
          <label htmlFor="genre">Genre</label>
          <input
            type="text"
            id="genre"
            name="genre"
            className={`form-control ${errors.genre ? 'is-invalid' : ''}`}
            value={formData.genre}
            onChange={handleChange}
          />
          {errors.genre && <div className="invalid-feedback">{errors.genre}</div>}
        </div>

        <div className="form-group">
          <label htmlFor="price">Price</label>
          <input
            type="number"
            id="price"
            name="price"
            className={`form-control ${errors.price ? 'is-invalid' : ''}`}
            value={formData.price}
            onChange={handleChange}
          />
          {errors.price && <div className="invalid-feedback">{errors.price}</div>}
        </div>

        <div className="form-group">
          <label htmlFor="stock">Stock</label>
          <input
            type="number"
            id="stock"
            name="stock"
            className={`form-control ${errors.stock ? 'is-invalid' : ''}`}
            value={formData.stock}
            onChange={handleChange}
          />
          {errors.stock && <div className="invalid-feedback">{errors.stock}</div>}
        </div>

        <div className="form-group">
          <label htmlFor="coverImageUrl">Cover Image URL</label>
          <input
            type="url"
            id="coverImageUrl"
            name="coverImageUrl"
            className={`form-control ${errors.coverImageUrl ? 'is-invalid' : ''}`}
            value={formData.coverImageUrl}
            onChange={handleChange}
          />
          {errors.coverImageUrl && <div className="invalid-feedback">{errors.coverImageUrl}</div>}
        </div>

        {formData.coverImageUrl && (
          <div className="form-group">
            <label>Image Preview:</label>
            <img src={formData.coverImageUrl} alt="Preview" style={{ width: '100px', height: 'auto' }} />
          </div>
        )}

        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            name="description"
            className={`form-control ${errors.description ? 'is-invalid' : ''}`}
            value={formData.description}
            onChange={handleChange}
          />
          {errors.description && <div className="invalid-feedback">{errors.description}</div>}
        </div>

        <div className="form-group checkbox">
          <label>
            <input
              type="checkbox"
              name="isOnSale"
              checked={formData.isOnSale}
              onChange={handleChange}
            />{' '}
            On Sale?
          </label>
        </div>

        {formData.isOnSale && (
          <>
            <div className="form-group">
              <label htmlFor="discountPercentage">Discount Percentage</label>
              <input
                type="number"
                id="discountPercentage"
                name="discountPercentage"
                className={`form-control ${errors.discountPercentage ? 'is-invalid' : ''}`}
                value={formData.discountPercentage}
                onChange={handleChange}
              />
              {errors.discountPercentage && <div className="invalid-feedback">{errors.discountPercentage}</div>}
            </div>

            <div className="form-group">
              <label htmlFor="discountEndDate">Discount End Date</label>
              <input
                type="date"
                id="discountEndDate"
                name="discountEndDate"
                className={`form-control ${errors.discountEndDate ? 'is-invalid' : ''}`}
                value={formData.discountEndDate}
                onChange={handleChange}
              />
              {errors.discountEndDate && <div className="invalid-feedback">{errors.discountEndDate}</div>}
            </div>
          </>
        )}
      </div>

      <div className="form-actions" style={{ marginTop: '20px' }}>
        <button type="submit" className="btn btn-primary" disabled={isSubmitting}>
          {isSubmitting ? 'Submitting...' : isEditMode ? 'Update Book' : 'Add Book'}
        </button>
        <button
          type="button"
          className="btn btn-secondary"
          onClick={onCancel}
          disabled={isSubmitting}
          style={{ marginLeft: '10px' }}
        >
          Cancel
        </button>
      </div>
    </form>
  )
}

