"use client"

import { useState, useEffect } from "react"
import { useParams, Link } from "react-router-dom"
import { orderService } from "../../services/orderService"

function OrderDetails() {
  const { id } = useParams()
  const [order, setOrder] = useState(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")

  useEffect(() => {
    fetchOrderDetails()
  }, [id])

  const fetchOrderDetails = async () => {
    setLoading(true)
    try {
      const data = await orderService.getOrderById(Number.parseInt(id))
      setOrder(data)
    } catch (error) {
      setError(error.message)
    } finally {
      setLoading(false)
    }
  }

  if (loading) {
    return <div className="loading">Loading order details...</div>
  }

  if (error) {
    return <div className="alert alert-danger">{error}</div>
  }

  if (!order) {
    return <div className="alert alert-danger">Order not found</div>
  }

  return (
    <div className="order-details-container">
      <div className="order-header">
        <h2>Order Details</h2>
        <div className="order-status-badge">
          <span className={`status-badge status-${order.status.toLowerCase()}`}>{order.status}</span>
        </div>
      </div>

      {order.status === "Processed" && (
        <div className="order-processed-notification">
          <div className="alert alert-success">
            <i className="check-icon">✓</i> Your order has been processed and is ready for pickup!
            {order.processedDate && (
              <div className="processed-date">Processed on: {new Date(order.processedDate).toLocaleString()}</div>
            )}
          </div>
        </div>
      )}

      {/* Order Table */}
      <table className="order-details-table">
        <tbody>
          <tr>
            <td className="label">Order Number:</td>
            <td>#{order.id}</td>
          </tr>
          <tr>
            <td className="label">Date:</td>
            <td>{new Date(order.orderDate).toLocaleDateString()}</td>
          </tr>
          <tr>
            <td className="label">Claim Code:</td>
            <td>{order.claimCode}</td>
          </tr>
          <tr>
            <td className="label">Status:</td>
            <td className={`status-${order.status.toLowerCase()}`}>{order.status}</td>
          </tr>
        </tbody>
      </table>

      {/* Shipping Information Table */}
      <h3>Shipping Information</h3>
      <table className="order-details-table">
        <tbody>
          <tr>
            <td className="label">Address:</td>
            <td>{order.shippingAddress}</td>
          </tr>
          <tr>
            <td className="label">Phone:</td>
            <td>{order.contactPhone}</td>
          </tr>
          {order.notes && (
            <tr>
              <td className="label">Notes:</td>
              <td>{order.notes}</td>
            </tr>
          )}
        </tbody>
      </table>

      {/* Order Items Table */}
      <h3>Order Items</h3>
      <table className="order-items-table">
        <thead>
          <tr>
            <th>Item</th>
            <th>Quantity</th>
            <th>Unit Price</th>
            <th>Subtotal</th>
          </tr>
        </thead>
        <tbody>
          {order.items.map((item) => (
            <tr key={item.id}>
              <td>{item.title} by {item.author}</td>
              <td>{item.quantity}</td>
              <td>${item.unitPrice.toFixed(2)}</td>
              <td>${item.subtotal.toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Order Summary Table */}
      <h3>Order Summary</h3>
      <table className="order-summary-table">
        <tbody>
          <tr>
            <td className="label">Subtotal:</td>
            <td>${order.subtotal.toFixed(2)}</td>
          </tr>
          <tr>
            <td className="label">Tax (8%):</td>
            <td>${order.tax.toFixed(2)}</td>
          </tr>
          <tr>
            <td className="label">Total:</td>
            <td className="total">${order.total.toFixed(2)}</td>
          </tr>
        </tbody>
      </table>

      {/* Order Actions */}
      <div className="order-actions">
        {order.status === "Pending" && order.canCancel && (
          <button
            className="btn btn-danger"
            onClick={async () => {
              if (window.confirm("Are you sure you want to cancel this order?")) {
                try {
                  await orderService.cancelOrder(order.id)
                  // Refresh order details
                  fetchOrderDetails()
                } catch (error) {
                  setError(error.message)
                }
              }
            }}
          >
            Cancel Order
          </button>
        )}
        <Link to="/orders" className="btn btn-secondary">
          Back to Orders
        </Link>
      </div>
    </div>
  )
}

export default OrderDetails
