"use client"

import { useState, useEffect } from "react"
import { bannerService } from "../../services/bannerService"

const BannerForm = ({ banner, onSuccess, onCancel }) => {
  const [formData, setFormData] = useState({
    message: "",
    startDate: new Date().toISOString().split("T")[0],
    endDate: new Date(new Date().setDate(new Date().getDate() + 7)).toISOString().split("T")[0],
    backgroundColor: "#f8d7da",
    textColor: "#721c24",
  })

  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")

  useEffect(() => {
    if (banner) {
      setFormData({
        message: banner.message,
        startDate: new Date(banner.startDate).toISOString().split("T")[0],
        endDate: new Date(banner.endDate).toISOString().split("T")[0],
        backgroundColor: banner.backgroundColor,
        textColor: banner.textColor,
      })
    }
  }, [banner])

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData({
      ...formData,
      [name]: value,
    })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setLoading(true)
    setError("")

    try {
      console.log("Submitting banner data:", formData)

      // Validate dates
      const startDate = new Date(formData.startDate)
      const endDate = new Date(formData.endDate)

      if (endDate <= startDate) {
        throw new Error("End date must be after start date")
      }

      const bannerData = {
        ...formData,
        startDate: startDate,
        endDate: endDate,
      }

      if (banner) {
        await bannerService.updateBanner(banner.id, bannerData)
      } else {
        await bannerService.createBanner(bannerData)
      }

      onSuccess()
    } catch (error) {
      console.error("Banner form error:", error)
      setError(error.message || "An error occurred while saving the banner")
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="banner-form">
      <h3 className="form-title">{banner ? "Edit Banner" : "Create New Banner"}</h3>

      {error && <div className="alert alert-danger">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="message">Banner Message</label>
          <textarea
            id="message"
            name="message"
            className="form-control"
            value={formData.message}
            onChange={handleChange}
            required
            rows={3}
            maxLength={500}
          />
          <small className="text-muted">Maximum 500 characters</small>
        </div>

        <div className="form-group">
          <label htmlFor="startDate">Start Date</label>
          <input
            type="date"
            id="startDate"
            name="startDate"
            className="form-control"
            value={formData.startDate}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="endDate">End Date</label>
          <input
            type="date"
            id="endDate"
            name="endDate"
            className="form-control"
            value={formData.endDate}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="backgroundColor">Background Color</label>
          <div className="color-picker-container">
            <input
              type="color"
              id="backgroundColor"
              name="backgroundColor"
              value={formData.backgroundColor}
              onChange={handleChange}
              className="color-picker"
            />
            <input
              type="text"
              value={formData.backgroundColor}
              onChange={handleChange}
              name="backgroundColor"
              className="form-control color-text"
            />
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="textColor">Text Color</label>
          <div className="color-picker-container">
            <input
              type="color"
              id="textColor"
              name="textColor"
              value={formData.textColor}
              onChange={handleChange}
              className="color-picker"
            />
            <input
              type="text"
              value={formData.textColor}
              onChange={handleChange}
              name="textColor"
              className="form-control color-text"
            />
          </div>
        </div>

        <div className="banner-preview">
          <h4>Preview</h4>
          <div
            className="banner-preview-content"
            style={{
              backgroundColor: formData.backgroundColor,
              color: formData.textColor,
              padding: "15px",
              borderRadius: "4px",
            }}
          >
            {formData.message || "Banner message will appear here"}
          </div>
        </div>

        <div className="form-actions">
          <button type="button" className="btn btn-secondary" onClick={onCancel}>
            Cancel
          </button>
          <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? "Saving..." : banner ? "Update Banner" : "Create Banner"}
          </button>
        </div>
      </form>
    </div>
  )
}

export default BannerForm
